import React, { Component } from 'react';

class App extends Component {
  state = {
    numeroClick: 0,
  }

  handleChange() {
    this.setState((estadoAnterior, props) => ({
      numeroClick: estadoAnterior.numeroClick + 1
    }))
  }
  render() {
    return (
      <div>
        <button onClick={this.handleChange}></button>
      </div>
    );
  }
}

export default App;
